package drivers

import (
    "context"
    "control-panel/ports/drivens"
)

type ControlPanelAdapter struct {
    drivenAdapter drivens.DrivenAdapterPort
}

func NewControlPanelAdapter(drivenAdapter drivens.DrivenAdapterPort) *ControlPanelAdapter {
    return &ControlPanelAdapter{
        drivenAdapter: drivenAdapter,
    }
}

func (cpa *ControlPanelAdapter) ExampleFunction(ctx context.Context, data string) (string, error) {
    result, err := cpa.drivenAdapter.ExampleDrivenFunction(ctx, data)
    if err != nil {
        return "", err
    }
    return result, nil
}
